import React, { useState } from 'react';

const StudentForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    dateOfBirth: '',
    major: '',
    year: ''
  });

  const [formErrors, setFormErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.firstName.trim()) errors.firstName = 'First name is required';
    if (!formData.lastName.trim()) errors.lastName = 'Last name is required';
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email is invalid';
    }
    if (!formData.dateOfBirth) errors.dateOfBirth = 'Date of birth is required';
    if (!formData.major.trim()) errors.major = 'Major is required';
    if (!formData.year) errors.year = 'Year is required';

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Form Submitted', formData);
      alert('Student Added Successfully!');
      // Here you would typically send the data to a backend service
    }
  };

  const majorOptions = [
    'Computer Science', 
    'Business Administration', 
    'Engineering', 
    'Biology', 
    'Psychology', 
    'Economics', 
    'Art', 
    'Mathematics'
  ];

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid md:grid-cols-2 gap-6">
        <div>
          <label htmlFor="firstName" className="block text-sm font-medium text-gray-700 mb-2">
            First Name
          </label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className={`w-full px-4 py-3 border ${
              formErrors.firstName 
                ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
                : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
            } rounded-xl shadow-sm transition duration-300`}
            placeholder="Enter first name"
          />
          {formErrors.firstName && (
            <p className="text-red-500 text-xs mt-1">{formErrors.firstName}</p>
          )}
        </div>
        <div>
          <label htmlFor="lastName" className="block text-sm font-medium text-gray-700 mb-2">
            Last Name
          </label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            className={`w-full px-4 py-3 border ${
              formErrors.lastName 
                ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
                : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
            } rounded-xl shadow-sm transition duration-300`}
            placeholder="Enter last name"
          />
          {formErrors.lastName && (
            <p className="text-red-500 text-xs mt-1">{formErrors.lastName}</p>
          )}
        </div>
      </div>

      <div>
        <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
          Email Address
        </label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className={`w-full px-4 py-3 border ${
            formErrors.email 
              ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
              : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
          } rounded-xl shadow-sm transition duration-300`}
          placeholder="student@example.com"
        />
        {formErrors.email && (
          <p className="text-red-500 text-xs mt-1">{formErrors.email}</p>
        )}
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        <div>
          <label htmlFor="dateOfBirth" className="block text-sm font-medium text-gray-700 mb-2">
            Date of Birth
          </label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
            max={new Date().toISOString().split('T')[0]}
            className={`w-full px-4 py-3 border ${
              formErrors.dateOfBirth 
                ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
                : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
            } rounded-xl shadow-sm transition duration-300`}
          />
          {formErrors.dateOfBirth && (
            <p className="text-red-500 text-xs mt-1">{formErrors.dateOfBirth}</p>
          )}
        </div>
        <div>
          <label htmlFor="year" className="block text-sm font-medium text-gray-700 mb-2">
            Academic Year
          </label>
          <select
            id="year"
            name="year"
            value={formData.year}
            onChange={handleChange}
            className={`w-full px-4 py-3 border ${
              formErrors.year 
                ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
                : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
            } rounded-xl shadow-sm transition duration-300`}
          >
            <option value="">Select Academic Year</option>
            <option value="freshman">Freshman</option>
            <option value="sophomore">Sophomore</option>
            <option value="junior">Junior</option>
            <option value="senior">Senior</option>
            <option value="graduate">Graduate</option>
          </select>
          {formErrors.year && (
            <p className="text-red-500 text-xs mt-1">{formErrors.year}</p>
          )}
        </div>
      </div>

      <div>
        <label htmlFor="major" className="block text-sm font-medium text-gray-700 mb-2">
          Major
        </label>
        <select
          id="major"
          name="major"
          value={formData.major}
          onChange={handleChange}
          className={`w-full px-4 py-3 border ${
            formErrors.major 
              ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
              : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
          } rounded-xl shadow-sm transition duration-300`}
        >
          <option value="">Select Major</option>
          {majorOptions.map((major) => (
            <option key={major} value={major}>{major}</option>
          ))}
        </select>
        {formErrors.major && (
          <p className="text-red-500 text-xs mt-1">{formErrors.major}</p>
        )}
      </div>

      <div className="pt-4">
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-4 rounded-xl 
          hover:from-blue-600 hover:to-indigo-700 transition duration-300 
          transform hover:-translate-y-1 hover:shadow-lg focus:outline-none focus:ring-2 
          focus:ring-blue-500 focus:ring-opacity-50"
        >
          Add Student
        </button>
      </div>
    </form>
  );
};

const AddStudentPage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-blue-100 py-6 flex flex-col justify-center sm:py-12 relative overflow-hidden">
      {/* Decorative Shapes */}
      <div className="absolute top-0 left-0 w-72 h-72 bg-blue-200/30 rounded-full blur-3xl transform -translate-x-1/3 -translate-y-1/3"></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-purple-200/30 rounded-full blur-3xl transform translate-x-1/4 translate-y-1/4"></div>
      
      <div className="relative py-3 sm:max-w-2xl sm:mx-auto w-full px-4">
        <div className="absolute inset-0 bg-gradient-to-r from-blue-300 to-blue-600 shadow-2xl transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl"></div>
        
