import React from 'react';
import StudentForm from '../components/StudentForm';

const AddStudentPage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 via-white to-slate-200 flex items-center justify-center px-4 py-12">
      <div className="w-full max-w-3xl relative">
        <div className="absolute inset-0 bg-gradient-to-r from-indigo-400 via-purple-500 to-pink-500 rounded-3xl shadow-xl blur-sm opacity-30"></div>
        
        <div className="relative bg-white rounded-3xl shadow-2xl px-10 py-12 sm:px-16 sm:py-16 z-10">
          <div className="mb-8 text-center">
            <h1 className="text-3xl sm:text-4xl font-extrabold text-gray-800 tracking-tight">
              Add New Student
            </h1>
            <p className="mt-2 text-gray-500 text-sm sm:text-base">
              Fill in the details below to add a student record
            </p>
          </div>
          <StudentForm />
        </div>
      </div>
    </div>
  );
};

export default AddStudentPage;
